sources.global.css:
@font-face {
	font-family: 'robotobold';
	src: url('../fonts/roboto-bold-webfont.woff') format('woff');
	font-weight: normal;
	font-style: normal;
}

@font-face {
	font-family: 'robotoregular';
	src: url('../fonts/roboto-regular-webfont.woff') format('woff');
	font-weight: normal;
	font-style: normal;
}

html {
	height: 100%;
}

body {
	height: 100%;
	background-color: var(--background-color);
	overflow-y: scroll;
	margin: 0;
	font-family: var(--norm-font);
	color: var(--container-text-color);
	-webkit-font-smoothing: antialiased;
	-moz-osx-font-smoothing: grayscale;
}

.hidden {
	display: none !important;
}

a:link {
	color: var(--anchor-color);
	background-color: transparent;
	text-decoration: underline;
}

a:visited {
	color: var(--anchor-color);
	background-color: transparent;
	text-decoration: underline;
}

a:hover {
	color: var(--anchor-hover);
	background-color: transparent;
	text-decoration: underline;
}

a:active {
	color: var(--anchor-hover);
	background-color: transparent;
	text-decoration: underline;
}

a.a--no-underline {
	text-decoration: none;
}

button {
	outline: none;
	border: none;
	cursor: pointer;
	font-family: var(--norm-font);
	background-color: var(--button-background-color);
	color: var(--button-text-color);
}

button:hover {
	background-color: var(--button-hover-background);
	color: var(--button-hover-text);
}

.wrapper {
	min-height: 100%;
	margin-bottom: -15vw;
}

.block {
	position: relative;
	height: auto;
	text-align: center;
	margin-bottom: 5vw;
}

#main-content {
	margin: auto;
	padding: 2vw;
	padding-bottom: 14vw;
}

.row {
	position: relative;
	height: auto;
	text-align: center;
	margin-bottom: 1vw;
}

.container {
	background: var(--container-background-color);
	color: var(--container-text-color);
	text-align: center;
}

.l-margin {
	margin-left: 1vw;
}

.r-margin {
	margin-right: 1vw;
}

/* .hover:hover {
  background: var(--container-hover);
  color: var(--container-hover-text-color);
} */

body.fullsize-content {
	overflow: auto;
}

body.fullsize-content #main-content {
	height: calc(100vh - 14.25vw - 12vw - 2px);
	height: var(--mobile-fullsize-body-height, calc(100vh - 14.25vw - 12vw - 2px));
	margin: 0;
	padding: 0;
}

header {
	position: relative;
	height: 12vw;
	width: 100%;
	padding-top: 2.25vw;
	padding-bottom: 0;
	margin: auto;
	text-align: center;
	background-color: var(--header-background-color);
}

header a:link {
	color: var(--header-text-color);
}

/* #header__anchor {
  position: absolute;
  top: 46%;
  left: 50%;
  font-family: var(--bold-font);
  text-decoration: none;
  font-size: 7.5vw;
  line-height: 7.5vw;
  font-weight: bold;
  color: var(--header-text-color);
  background-color: transparent;
  transform: translate(-50%,-50%);
} */
nav {
	position: fixed;
	bottom: 0;
	width: 100%;
	border-top: solid 2px var(--navigation-icon-color);
	z-index: 10;
}

#nav-tabs {
	height: 12vw;
	background: var(--navigation-background-color);
	color: var(--navigation-text-color);
	text-align: center;
}

body.fullsize-content #nav-tabs {
	background-color: var(--navigation-background-color);
}

#nav-tabs a,
header a {
	text-decoration: none;
	font-size: 0;
}

#nav-tabs a div,
header a div {
	display: inline-block;
	width: calc(100% / 5);
	height: auto;
}

/* #nav-tabs a div:hover{
  background-color: var(--anchor-hover);
} */
#nav-tabs a div img,
header a div img {
	margin: 1vw;
	fill: var(--navigation-icon-color);
}

header a div img {
	fill: var(--header-icon-color);
}

#nav-tabs a.selected div img,
header a.selected div img {
	fill: var(--navigation-active-icon-color);
}

header a div {
	height: 14.25vw;
	width: auto;
	top: 0;
	right: 5px;
	position: absolute;
	display: flex;
	align-items: center;
}

#question-mark-svg {
	transform: scale(1.3);
	transform-origin: center;
}

.modal-polygon {
	fill: var(--navigation-active-icon-color);
}

footer {
	background-color: var(--navigation-background-color);
	border-top: solid 5px white;
	display: flex;
	flex-direction: column;
	text-align: center;
	font-size: 12px;
	padding: 10px 0;
}

footer img {
	width: 200px;
	margin: 0 auto 10px;
}

img.svg-mask-only {
	position: relative;
	box-sizing: border-box;
}

img.svg-mask-only:after {
	display: block;
	position: absolute;
	top: 0;
	left: 0;
	bottom: 0;
	right: 0;
	content: "\u00a0";
	background: currentColor;
}


@media screen and (min-width: 500px) {
	.wrapper {
		margin-bottom: -75px;
	}

	#main-content {
		padding: 15px;
		padding-bottom: 75px;
	}

	header {
		height: 60px;
		padding-top: 11.25px;
	}

	header p {
		top: -3.75px;
		font-size: 12.5px;
	}

	#header__anchor {
		font-size: 37.5px;
		line-height: 37.5px;
	}

	.text, .shadow {
		top: 5px;
		font-size: 50px;
	}

	nav {
		width: 100%;
	}

	#nav-tabs {
		height: 60px;
	}

	header a div {
		height: 71.25px;
	}

	#nav-tabs a div svg,
	header a div svg {
		margin: 10px;
		height: 40px;
	}

	.block {
		margin: auto;
		margin-bottom: 25px;
		width: calc(50% + 240px);
	}

	.row {
		margin-left: auto;
		margin-right: auto;
		margin-bottom: 5px;
	}

	.l-margin {
		margin-left: 5px;
	}

	.r-margin {
		margin-right: 5px;
	}
}

@media (prefers-reduced-motion: no-preference) {
	.wrapper:before {
		content: '';
		height: 0;
		width: 100%;
		display: block;
		background: rgba(200, 200, 200, 1);
		background: linear-gradient(90deg, transparent 25%, rgba(200, 200, 200, 1) 25%, rgba(200, 200, 200, 1) 37.5%, transparent 37.5%, transparent 62.5%, rgba(200, 200, 200, 1) 62.5%, rgba(200, 200, 200, 1) 75%, transparent 75%);
		transition: height 0.5s ease-out;
		animation: loading 2.5s infinite linear;
		background-size: 400% 100%;
	}

	.wrapper.loading:before {
		height: 6px;
	}

	@keyframes loading {
		0% {
			background-position: 100% 0%;
		}
		35% {
			background-position: 50% 0%;
		}
		100% {
			background-position: 0% 0%;
		}
	}
}

svg {
	fill: currentColor;
}

canvas {
	pointer-events: none;
	z-index: 5;
	position: fixed;
	width: 100vw;
	height: 100vh;
	top: 0;
	left: 0;
}
