  margin: 0;
  font-size: 5vw;
  line-height: 5vw;
  font-family: var(--bold-font);
}
#hall-pass .unit__info p {
  margin: 0;
  font-size: 5vw;
  line-height: 5vw;
  font-family: var(--bold-font);
  color: var(--highlight-text-color)
}

/* Video */

#thank-you {
  width: 100%;
}

#bugs h2{
  margin: 0;
  padding: 1.33vw;
  font-size: 4vw;
}
#bugs p{
  margin: 0;
  font-size: 3.2vw;
  padding-bottom: 1.33vw;
}

#video-container {
  position: fixed;
  display: none;
  top: 50%;
  left: 50%;
  width: 80vw;
  height: 45vw;
  transform: translate(-50%, -50%);
  z-index: 100;
}
#video-container.active {
  display: block;
}
