  z-index: 1;
  text-align: center;
  transform: translateY(150%);
  transition: transform 0.5s;
}
#help-popup.active {
  display: block;
  transform: translateY(0);
}
#help-popup a {
  color: white;
}

@media screen and (min-width: 500px) {
	#help-popup {
		padding: 10px;
		font-size: 20px;
		bottom: 60px;
	}

	#period {
		margin-top: 5px;
	}

	#period__box {
		letter-spacing: 1px;
		margin-left: 5px;
		margin-right: 5px;
	}

	#period__header {
		width: 300px;
		font-size: 20px;
		margin: 10px auto 2.5px;
		letter-spacing: 1px;
		transition: 0.3s;
	}

	#period__header.active:focus-visible {
		border-bottom: 2.5px solid var(--secondary-background-color);
		background-color: var(--container-text-color);
	}

	#period__time {
		font-size: 40px;
		margin: 2.5px 0 10px;
	}

	#period__gallery {
		min-height: 42px;
	}

	.gallery-dot {
		width: 21px;
		padding: 21px 0 0;
		margin: 0 7.5px 15px;
		transition: 0.3s;
	}

	.gallery-dot:before {
		top: -5px;
		left: -5px;
		right: -5px;
		bottom: -5px;
		border-radius: 5px;
	}

	.arrow {
		font-size: 75px;
		flex: none;
		width: 67px;
	}

	#countdown {
		letter-spacing: 2.5px;
		margin: 0 auto 0px;
	}

	#countdown__timer {
		font-size: 60px;
	}

	#countdown__bar, #hall-pass .countdown__bar {
		margin: 0 auto 10px;
		height: 12.5px;
	}

	#countdown__progress, #hall-pass .countdown__bar .countdown__progress {
		height: 12.5px;
	}

	#sub div button {
		padding: 10px;
		font-size: 40px;
	}

	#extra-features a div {
		padding: 5px;
		font-size: 25px;
	}

	#splash svg {
		padding-left: 5px;
		width: 50px;
		height: 50px;
		margin: 10px 0;
	}

	#splash__body, #splash__body--alt, #splash__body--alt2 {
		padding: 10px;
		padding-left: 0;
		font-size: 20px;
	}

	#splash__puzzle-button {
		margin: 0 0 0 5px;
	}

	#splash__puzzle-button img {
		padding: 10px;
		width: 40px;
		height: 40px;
	}

	#splash__puzzle-button p {
		font-size: 15px;
		width: 40px;
	}

	#splash__puzzle-button.not-new img {
		width: 40px;
	}

	#splash__reopen button {
		font-size: 20px;
	}

	#link-header img {
		padding: 10px;
		height: 50px;
	}

	#link-info p {
		padding: 15px 0;
		font-size: 25px;
	}

	#link-info__company img {
		padding: 5px 0;
		width: 65px;
	}

	#link-button {
		padding: 5px;
		font-size: 35px;
	}

	#link-submit {
		font-size: 35px;
	}

	#link-coupon {
		width: 450px;
		padding: 20px 0;
	}

	/* Hall Pass */
	#hall-pass .unit__info {
		margin: 10px auto 7.5px;
	}

	#hall-pass .unit__info h1 {
		font-size: 25px;
		line-height: 25px;
	}

	#hall-pass .unit__info p {
		font-size: 25px;
		line-height: 25px;
	}

}

/* Keyframes Storage */
@keyframes shake {
	0% {
		transform: scale(1, 1) rotate(0deg);
	}
	10% {
		transform: scale(1, 1) rotate(0deg);
	}
	20% {
		transform: scale(1, 1) rotate(0deg);
	}
	30% {
		transform: scale(1, 1) rotate(0deg);
	}
	40% {
		transform: scale(1.1, 1.1) rotate(5deg);
	}
	50% {
		transform: scale(1.1, 1.1) rotate(-6deg);
	}
	60% {
		transform: scale(1.1, 1.1) rotate(7deg);
	}
	70% {
		transform: scale(1.2, 1.2) rotate(-8deg);
	}
	80% {
		transform: scale(1.3, 1.3) rotate(9deg);
	}
	90% {
		transform: scale(1.3, 1.3) rotate(-10deg);
	}
	95% {
		transform: scale(1.3, 1.3) rotate(9deg);
	}
	96% {
		transform: scale(1.4, 1.4) rotate(-8deg);
	}
	97% {
		transform: scale(1.3, 1.3) rotate(7deg);
	}
	98% {
		transform: scale(1.2, 1.2) rotate(-6deg);
	}
	99% {
		transform: scale(1.1, 1.1) rotate(5deg);
	}
	100% {
		transform: scale(1, 1) rotate(0deg);
	}


}

